sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	var flag = 0;
	var message = 'Action Aborted:';
	var gr = new GlideAggregate('x_amfai_cacmdba_root_cause');
	gr.addQuery('case_id', current.sys_id);
	gr.addAggregate('COUNT');
	gr.query();
	var rootCause = 0;
	if (gr.next()){
		rootCause = gr.getAggregate('COUNT');
		if (rootCause == 0){
			flag = 1;
			message = message + ' There should be at least one Root Cause;';
			//current.setAbortAction(true);
		}
	}

	var gr2 = new GlideAggregate('x_amfai_cacmdba_case_referral');
	gr2.addQuery('cacm_id', current.sys_id);
	gr2.addQuery('status', 'closed');
	gr2.addAggregate('COUNT');
	gr2.query();
	var referral = 0;
	if (gr2.next()){
		referral = gr2.getAggregate('COUNT');
		if (referral == 0){
			flag = 1;
			message = message + ' There should be at least one Closed Referral;';
			//current.setAbortAction(true);
		}
	}

	var gr3 = new GlideAggregate('x_amfai_cacmdba_cacm_attachments');
	gr3.addQuery('case_id', current.sys_id);
	gr3.addQuery('document_status', 'draft');
	gr3.addAggregate('COUNT');
	gr3.query();
	var attachment = 0;
	if (gr3.next()){
		attachment = gr3.getAggregate('COUNT');
		if (attachment != 0){
			flag = 1;
			message = message + ' One or More Attachments are in "Draft" State';
			//current.setAbortAction(true);
		}
	}

	if (flag == 1){
		gs.addErrorMessage(message);
		current.setAbortAction(true);
	}

	// Add your code here

})(current, previous);
