	//var state = g_form.getValue('complaint_state');
	//var instType = g_form.getValue('instruction_type');

	var ADVOC2DPajax = new GlideAjax('generate_letter');
	ADVOC2DPajax.addParam('sysparm_name', 'generateADVOC2DP');
	ADVOC2DPajax.addParam('sysparm_currentCaseID', ADVOC2DPCaseID);
	ADVOC2DPajax.addParam('sysparm_currentEventID', ADVOC2DPEventID);
	ADVOC2DPajax.addParam('sysparm_currentExecutive', ADVOC2DPExecutive);
	//ajax.addParam('sysparm_state', state);
	//ajax.addParam('sysparm_instType', instType);
	ADVOC2DPajax.getXML(loadResponse);
	GlideDialogWindow.get().destroy();
	}

