sys_ui_page.client_script:
function showClaimInfoListView(){
	var fname = document.getElementById('fname').value;
		var lname = document.getElementById('lname').value;
		var state = document.getElementById('state').value;
		var claimNumber = document.getElementById('claimNumber').value;
		if((fname == '' || lname == '' || state == '') && (claimNumber == '')){
		document.getElementById("mandatory-text").style.color = "#ff0000";
		return true;
		}
	if(claimNumber == ''){
	var ga = new GlideAjax('x_amfai_cacmdba.CACMAjaxIntegrationUtil');
	ga.addParam('sysparm_name','triggerClaimInfoSOAPCall');
	ga.addParam('sysparm_fn_name',fname);
	ga.addParam('sysparm_ln_name',lname);
	ga.addParam('sysparm_state',state);
ga.addParam('sysparm_caseID',g_form.getUniqueValue());
	ga.getXML(getSoapResponse);
	}else{

		var ga1 = new GlideAjax('x_amfai_cacmdba.CACMAjaxIntegrationUtil');
	ga1.addParam('sysparm_name','triggerClaimInfobyNumSOAPCall');
	ga1.addParam('sysparm_claim_number',claimNumber);
// 	ga1.addParam('sysparm_ln_name',lname);
// 	ga1.addParam('sysparm_state',state);
ga1.addParam('sysparm_caseID',g_form.getUniqueValue());
	ga1.getXML(getSoapResponse);
	}

	function getSoapResponse(response) {
		var answer = response.responseXML.documentElement.getAttribute("answer");
		//alert(answer);
		GlideModal.get().destroy();
		var w = new GlideModal('show_list');
		w.setTitle('Claims');
		w.setPreference('table','x_amfai_cacmdba_claim_cst_info_stg_list');
		w.setWidth(1500);
		w.setPreference('sysparm_query',"sys_created_onONCurrent minute@javascript:gs.beginningOfCurrentMinute()@javascript:gs.endOfCurrentMinute()^case_id="+g_form.getUniqueValue()+"^random_number="+answer);
		w.render();
	}


}
